import React, { useState ,useRef} from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonInput, IonItem, IonLabel, IonList, IonItemDivider, IonGrid, IonRow, IonCol, IonButton, IonIcon } from '@ionic/react';
import { saveOutline } from 'ionicons/icons';

const Formedit: React.FC = () => {

  const [text, setText] = useState<string>();
  const [number, setNumber] = useState<number>();

  const contactnoref=useRef<HTMLIonInputElement>(null);
  const nameref=useRef<HTMLIonInputElement>(null);
  const emailref=useRef<HTMLIonInputElement>(null);
  const Bloodref=useRef<HTMLIonInputElement>(null);
//   const emailref=useRef<HTMLIonInputElement>(null);
//   const emailref=useRef<HTMLIonInputElement>(null);


  const savedetails=()=>{
    const enteredname=nameref.current!.value;
    const enteredcontact=contactnoref.current!.value;
    const enteredemail=emailref.current!.value;
    const enteredblood=Bloodref.current!.value;

    if(!enteredname || ! enteredcontact ||!enteredemail || ! enteredcontact){return}

  }


  return (
    <IonPage>
      <IonHeader collapse="condense">
          <IonToolbar>
            <IonTitle size="large">Register</IonTitle>
          </IonToolbar>
        </IonHeader>
      <IonContent className="ion-padding">
      <IonGrid>
          <IonRow>
              <IonCol>
              <IonItem>
              <IonLabel position="floating">Full Name</IonLabel>
              <IonInput type ="text" ref={nameref}></IonInput>
            </IonItem>
              </IonCol>
          </IonRow>
     
          <IonRow>
              <IonCol>
              <IonItem>
              <IonLabel position="floating">Contact no;</IonLabel>
              <IonInput type ="number" ref={contactnoref}></IonInput>
            </IonItem>
              </IonCol>
          </IonRow>
          <IonRow>
              <IonCol>
              <IonItem>
              <IonLabel position="floating">Email</IonLabel>
              <IonInput type ="email" ref={emailref}></IonInput>
            </IonItem>
              </IonCol>
          </IonRow>
          <IonRow>
              <IonCol>
              <IonItem>
              <IonLabel position="floating">Blood Group</IonLabel>
              <IonInput  ref={Bloodref}></IonInput>
            </IonItem>
              </IonCol>
          </IonRow>

          <IonRow>
          <IonCol className="ion-text-center"> 
            <IonButton onClick={savedetails}>
              <IonIcon slot="start" icon={saveOutline}/>
              Submit
            </IonButton>
            </IonCol>
          </IonRow>

          
          </IonGrid>
      </IonContent>
    </IonPage>
  );
};
export default Formedit;
